"use client"

import { useState } from "react"
import { View, Text, StyleSheet, TouchableOpacity, Animated } from "react-native"
import { CheckCircle, ChevronRight, Sparkles } from "../components/icons"
import { theme } from "../styles/theme"
import { Badge } from "./ui/Badge"

interface MissionProps {
  id: number
  title: string
  description: string
  difficulty: string
  category: string
}

interface DailyMissionProps {
  mission: MissionProps
}

const DailyMission = ({ mission }: DailyMissionProps) => {
  const [completed, setCompleted] = useState(false)
  const scaleAnim = new Animated.Value(1)

  const handleComplete = () => {
    Animated.sequence([
      Animated.timing(scaleAnim, {
        toValue: 0.95,
        duration: 100,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim, {
        toValue: 1,
        duration: 100,
        useNativeDriver: true,
      }),
    ]).start()

    setCompleted(!completed)
  }

  return (
    <Animated.View
      style={[styles.container, completed && styles.completedContainer, { transform: [{ scale: scaleAnim }] }]}
    >
      <View style={styles.content}>
        <View style={styles.titleContainer}>
          <Sparkles color={theme.colors.primary} size={16} />
          <Text style={styles.title}>{mission.title}</Text>
        </View>
        <Text style={styles.description}>{mission.description}</Text>
        <View style={styles.badgeContainer}>
          <Badge label={mission.difficulty} variant="primary" />
          <Badge label={mission.category} variant="secondary" />
        </View>
      </View>
      <TouchableOpacity style={[styles.button, completed && styles.completedButton]} onPress={handleComplete}>
        {completed ? (
          <>
            <CheckCircle color={theme.colors.primary} size={16} />
            <Text style={styles.completedButtonText}>완료</Text>
          </>
        ) : (
          <>
            <Text style={styles.buttonText}>시작하기</Text>
            <ChevronRight color="#FFFFFF" size={16} />
          </>
        )}
      </TouchableOpacity>
    </Animated.View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#FFFFFF",
    borderRadius: 12,
    padding: 16,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.05,
    shadowRadius: 5,
    elevation: 2,
  },
  completedContainer: {
    backgroundColor: "rgba(241, 178, 159, 0.1)",
  },
  content: {
    flex: 1,
    marginRight: 12,
  },
  titleContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 8,
  },
  title: {
    fontSize: 16,
    fontWeight: "600",
    color: "#1F2937",
    marginLeft: 8,
  },
  description: {
    fontSize: 14,
    color: "#6B7280",
    marginBottom: 12,
  },
  badgeContainer: {
    flexDirection: "row",
    gap: 8,
  },
  button: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: theme.colors.primary,
    paddingVertical: 8,
    paddingHorizontal: 12,
    borderRadius: 20,
    shadowColor: theme.colors.primary,
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 3,
    elevation: 2,
  },
  completedButton: {
    backgroundColor: "#FFFFFF",
    borderWidth: 1,
    borderColor: theme.colors.primary,
  },
  buttonText: {
    color: "#FFFFFF",
    fontSize: 14,
    fontWeight: "500",
    marginRight: 4,
  },
  completedButtonText: {
    color: theme.colors.primary,
    fontSize: 14,
    fontWeight: "500",
    marginLeft: 4,
  },
})

export default DailyMission
