"use client"

import { useState } from "react"
import { View, Text, StyleSheet, Image, TouchableOpacity, Animated } from "react-native"
import { Heart, MessageCircle, Bookmark } from "../components/icons"
import { theme } from "../styles/theme"
import { useNavigation } from "@react-navigation/native"

interface DiaryProps {
  id: number
  date: string
  image: any
  content: string
  likes: number
  comments: number
}

interface DiaryCardProps {
  diary: DiaryProps
}

const DiaryCard = ({ diary }: DiaryCardProps) => {
  const navigation = useNavigation()
  const [liked, setLiked] = useState(false)
  const [saved, setSaved] = useState(false)
  const [likeCount, setLikeCount] = useState(diary.likes)

  const scaleAnim = new Animated.Value(1)

  const handlePress = () => {
    Animated.sequence([
      Animated.timing(scaleAnim, {
        toValue: 0.95,
        duration: 100,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim, {
        toValue: 1,
        duration: 100,
        useNativeDriver: true,
      }),
    ]).start(() => {
      navigation.navigate("DiaryDetail" as never, { id: diary.id } as never)
    })
  }

  const handleLike = () => {
    if (liked) {
      setLikeCount(likeCount - 1)
    } else {
      setLikeCount(likeCount + 1)
    }
    setLiked(!liked)
  }

  const handleSave = () => {
    setSaved(!saved)
  }

  return (
    <Animated.View style={[styles.container, { transform: [{ scale: scaleAnim }] }]}>
      <TouchableOpacity activeOpacity={0.9} onPress={handlePress}>
        <View style={styles.imageContainer}>
          <Image source={diary.image} style={styles.image} />
          <View style={styles.overlay} />
          <View style={styles.imageContent}>
            <View style={styles.avatarContainer}>
              <Image source={require("../../assets/images/profile.jpg")} style={styles.avatar} />
            </View>
            <View>
              <Text style={styles.diaryTitle}>나의 일기</Text>
              <Text style={styles.date}>{diary.date}</Text>
            </View>
          </View>
        </View>
      </TouchableOpacity>

      <View style={styles.content}>
        <Text style={styles.description} numberOfLines={2}>
          {diary.content}
        </Text>
      </View>

      <View style={styles.footer}>
        <View style={styles.actions}>
          <TouchableOpacity style={styles.actionButton} onPress={handleLike}>
            <Heart
              color={liked ? theme.colors.primary : "#6B7280"}
              fill={liked ? theme.colors.primary : "none"}
              size={16}
            />
            <Text style={styles.actionText}>{likeCount}</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.actionButton}>
            <MessageCircle color="#6B7280" size={16} />
            <Text style={styles.actionText}>{diary.comments}</Text>
          </TouchableOpacity>
        </View>
        <TouchableOpacity onPress={handleSave}>
          <Bookmark
            color={saved ? theme.colors.primary : "#6B7280"}
            fill={saved ? theme.colors.primary : "none"}
            size={16}
          />
        </TouchableOpacity>
      </View>
    </Animated.View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#FFFFFF",
    borderRadius: 12,
    overflow: "hidden",
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 3,
  },
  imageContainer: {
    height: 180,
    position: "relative",
  },
  image: {
    width: "100%",
    height: "100%",
    resizeMode: "cover",
  },
  overlay: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: "rgba(0, 0, 0, 0.3)",
    backgroundGradient: "linear-gradient(to bottom, rgba(0,0,0,0.1), rgba(0,0,0,0.5))",
  },
  imageContent: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    padding: 16,
    flexDirection: "row",
    alignItems: "center",
  },
  avatarContainer: {
    width: 32,
    height: 32,
    borderRadius: 16,
    backgroundColor: "#F3F4F6",
    borderWidth: 2,
    borderColor: "#FFFFFF",
    overflow: "hidden",
    marginRight: 8,
  },
  avatar: {
    width: "100%",
    height: "100%",
  },
  diaryTitle: {
    fontSize: 14,
    fontWeight: "500",
    color: "#FFFFFF",
  },
  date: {
    fontSize: 12,
    color: "rgba(255, 255, 255, 0.8)",
  },
  content: {
    padding: 16,
  },
  description: {
    fontSize: 14,
    color: "#4B5563",
    lineHeight: 20,
  },
  footer: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    paddingHorizontal: 16,
    paddingBottom: 16,
  },
  actions: {
    flexDirection: "row",
    gap: 16,
  },
  actionButton: {
    flexDirection: "row",
    alignItems: "center",
    gap: 4,
  },
  actionText: {
    fontSize: 12,
    color: "#6B7280",
  },
})

export default DiaryCard
