"use client";

import { useEffect } from "react";
import { View, Text, StyleSheet, Animated } from "react-native";
import { theme } from "../styles/theme";
import { BookIcon } from "../components/icons";

const SplashScreen = () => {
  const scaleAnim = new Animated.Value(0.8);
  const opacityAnim = new Animated.Value(0);
  const textOpacityAnim = new Animated.Value(0);
  const textPositionAnim = new Animated.Value(20);

  useEffect(() => {
    Animated.sequence([
      Animated.parallel([
        Animated.timing(scaleAnim, {
          toValue: 1,
          duration: 800,
          useNativeDriver: true,
        }),
        Animated.timing(opacityAnim, {
          toValue: 1,
          duration: 800,
          useNativeDriver: true,
        }),
      ]),
      Animated.parallel([
        Animated.timing(textOpacityAnim, {
          toValue: 1,
          duration: 800,
          useNativeDriver: true,
        }),
        Animated.timing(textPositionAnim, {
          toValue: 0,
          duration: 800,
          useNativeDriver: true,
        }),
      ]),
    ]).start();
  }, []);

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.iconContainer,
          {
            opacity: opacityAnim,
            transform: [{ scale: scaleAnim }],
          },
        ]}
      >
        <BookIcon width={120} height={120} />
      </Animated.View>
      <Animated.View
        style={{
          opacity: textOpacityAnim,
          transform: [{ translateY: textPositionAnim }],
        }}
      >
        <Text style={styles.title}>Dearie</Text>
        <Text style={styles.subtitle}>당신의 마음을 기록하세요</Text>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#FFFFFF",
  },
  iconContainer: {
    marginBottom: 24,
  },
  title: {
    fontSize: 40,
    fontWeight: "bold",
    color: theme.colors.primary,
    textAlign: "center",
  },
  subtitle: {
    fontSize: 16,
    color: "#9CA3AF",
    marginTop: 8,
    textAlign: "center",
  },
});

export default SplashScreen;
