import { View, Text, StyleSheet, TouchableOpacity, FlatList } from "react-native"
import { X } from "../components/icons"

interface EmotionSelectorProps {
  onSelect: (emotion: string) => void
  onClose: () => void
}

const EmotionSelector = ({ onSelect, onClose }: EmotionSelectorProps) => {
  const emotions = [
    { name: "Í∏∞ÏÅ®", emoji: "üòä" },
    { name: "Ïä¨Ìîî", emoji: "üò¢" },
    { name: "ÌôîÎÇ®", emoji: "üò†" },
    { name: "Î∂àÏïà", emoji: "üò∞" },
    { name: "ÌèâÏò®", emoji: "üòå" },
    { name: "ÏßÄÎ£®Ìï®", emoji: "üòë" },
    { name: "ÏÑ§Î†ò", emoji: "üòç" },
    { name: "Í∞êÏÇ¨", emoji: "üôè" },
    { name: "ÎÜÄÎûå", emoji: "üò≤" },
    { name: "ÌòºÎûÄ", emoji: "üòµ" },
    { name: "Ìù¨Îßù", emoji: "üåà" },
    { name: "ÌîºÍ≥§", emoji: "üò¥" },
  ]

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Ïò§ÎäòÏùò Í∞êÏ†ïÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî</Text>
        <TouchableOpacity onPress={onClose} style={styles.closeButton}>
          <X color="#6B7280" size={20} />
        </TouchableOpacity>
      </View>

      <FlatList
        data={emotions}
        numColumns={4}
        keyExtractor={(item) => item.name}
        renderItem={({ item }) => (
          <TouchableOpacity style={styles.emotionButton} onPress={() => onSelect(item.name)}>
            <Text style={styles.emoji}>{item.emoji}</Text>
            <Text style={styles.emotionName}>{item.name}</Text>
          </TouchableOpacity>
        )}
        contentContainerStyle={styles.emotionsGrid}
      />
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    padding: 16,
  },
  header: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 20,
  },
  title: {
    fontSize: 18,
    fontWeight: "600",
    color: "#1F2937",
  },
  closeButton: {
    padding: 4,
  },
  emotionsGrid: {
    paddingBottom: 16,
  },
  emotionButton: {
    flex: 1,
    aspectRatio: 1,
    margin: 4,
    justifyContent: "center",
    alignItems: "center",
    borderWidth: 1,
    borderColor: "#E5E7EB",
    borderRadius: 12,
    padding: 8,
  },
  emoji: {
    fontSize: 24,
    marginBottom: 4,
  },
  emotionName: {
    fontSize: 12,
    color: "#4B5563",
  },
})

export default EmotionSelector
